// third argument is a polyfill for using URLSearchParam with IE11
require(['jquery', 'xwiki-meta'], function ($, xm) {
  var saveButtonUIXDoc = new XWiki.Document(new XWiki.DocumentReference('xwiki', ['ChangeRequest', 'Code'], 'SaveButtonsUIX'));
  var saveConfig = JSON.parse($('#changerequest-save-config').text());
  
  var saveChangeRequest = function (event, inplaceEditing) {
    var isNewCR = $('#existingCRSelector').val() === "";
    var actionURL;
    var form;
    if (inplaceEditing) {
      form = $('.sticky-buttons-wrapper .sticky-buttons').data('fakeForm');
    } else {
      form = $('#save_changerequest').parents('form');
    }
    var urlParams = new URLSearchParams(window.location.search);
    if (isNewCR) {
      var crTitleField = $("<input>", {
        'type': 'hidden',
        'name': 'crTitle',
        'value': $('#crTitle').val()
      });
      form.append(crTitleField);

      var crDraftField = $("<input>", {
        'type': 'hidden',
        'name': 'crDraft',
        'value': $('#crDraft:checked').val()
      });
      form.append(crDraftField);
      actionURL = saveConfig.createURL + "?async=1";
    } else {
      actionURL = saveConfig.addChangesURL.replace("__CR__", $('#existingCRSelector').val()) + "?async=1";
      if (urlParams.has('changerequest')) {
        actionURL += "&fromchangerequest=1";
      }
    }
    var docField = $("<input>", {
      'type': 'hidden',
      'name': 'docReference',
      'value': xm.documentReference
    });
    form.append(docField);
    
    $(document).on('xwiki:document:saved', function(event, data) {
      if (data.changeRequestId) {
        window.location.href = data.changeRequestUrl;
      }
    });
    // if there's a template we specify to use sync save, in order to trick actionButtons.js
    if (inplaceEditing) {
      form['action'] = actionURL;
      if (form['template'] !== "") {
        form['async'] = "false";
      }
      $('#xwikicontent').trigger('xwiki:actions:save', {form: form, continue: true});
    } else {
      form.attr('action', actionURL);
      if (form.find('input[name=template]').val() !== "") {
        form.find('input[name=async]').val('false');
      }
      $(document).trigger('xwiki:actions:save', {form: form[0], continue: true});
    }
    
  };
  
  var validateForm = function () {
    var crTitle = $('#crTitle').val();
    var crSelector = $('#existingCRSelector').val();
    var saveButton = $('#saveChangeRequest');
    if (crTitle === "" && crSelector === "") {
      saveButton.attr('disabled', 'disabled');
    } else {
      saveButton.removeAttr('disabled');
    }
  };
  
  var loadRequiredSkinExtensions = function(requiredSkinExtensions) {
    var existingSkinExtensions;
    var getExistingSkinExtensions = function() {
      return $('link, script').map(function() {
        return $(this).attr('href') || $(this).attr('src');
      }).get();
    };
    $('<div/>').html(requiredSkinExtensions).find('link, script').filter(function() {
      if (!existingSkinExtensions) {
        existingSkinExtensions = getExistingSkinExtensions();
      }
      var url = $(this).attr('href') || $(this).attr('src');
      return existingSkinExtensions.indexOf(url) < 0;
    }).appendTo('head');
  };
  
  var createChangeRequestModal = function (inplaceEditing) {
    if ($('#changeRequestModal').length > 0) {
      $('#changeRequestModal').modal('show');
    } else {
      
      var urlParams = new URLSearchParams(window.location.search);
      var queryString = 'getmodal=1&outputSyntax=plain&docChange=' + xm.documentReference;
      if (urlParams.has('changerequest')) {
        queryString += '&changerequest=' + urlParams.get('changerequest');
      }
      $.get(saveButtonUIXDoc.getURL('get', queryString)).done(function (data, textStatus, jqXHR) {
        loadRequiredSkinExtensions(jqXHR.getResponseHeader('X-XWIKI-HTML-HEAD'));
        $('body').append(data);
        $('#changeRequestModal').modal('show');
        $('#saveChangeRequest').on('click', function (event) {
          saveChangeRequest(event, inplaceEditing);
        });
        $('#crTitle').on('change', validateForm);
        $('#existingCRSelector').on('change', validateForm);
        $(document).trigger('xwiki:dom:updated', {'elements': $('#changeRequestModal').toArray()});
      }).fail(function (data) {
        console.log(data);
        new XWiki.widgets.Notification("Error while saving the change request:" + data, 'error')
      });
    }
  };
  
  var attachButtons = function () {
    if ($('#save_changerequest').length == 0) {
      var button = $("<input>", {
        'type': 'button',
        'class': 'btn btn-info',
        'name': 'save_changerequest',
        'id': 'save_changerequest',
        'value': saveConfig.saveButtonTranslation
      });
      
      var buttonAdded = false;
      var inplaceEditing;
      if ($('.bottombuttons').length > 0) {
        $('.bottombuttons .buttons').prepend(button);
        buttonAdded = true;
        inplaceEditing = false;
      } else if ($('.inplace-editing-buttons').length > 0) {
        button.data('inplaceediting', true);
        $('.inplace-editing-buttons .buttons').prepend(button);
        buttonAdded = true;
        inplaceEditing = true;
      }
    }
    if (buttonAdded) {
      button.on('click', function() {
        createChangeRequestModal(inplaceEditing);
      });
      var urlParams = new URLSearchParams(window.location.search);
      var editExistingCR = urlParams.has('changerequest');
      var hasEditRight = saveConfig.hasEditRight;
      // hide minor edit and autosave capabilities
      if (!hasEditRight || editExistingCR) {
        $('.meta-minorEdit').remove();
        $('#autosaveControl').remove();
        $('input[name=action_save]').parent().remove();
      }
    }
  };
  
  var initialize = function() {
    var urlParams = new URLSearchParams(window.location.search);
    if (urlParams.has('template')) {
      // check template
      $.get(saveButtonUIXDoc.getURL('get'), {
        'checktemplate': 1,
        'outputSyntax': 'plain',
        'template': urlParams.get('template')
      }).done(function (data) {
        if (data.isSupported === true) {
          attachButtons();
        } else {
          console.info("Template [{}] not supported by change request.", urlParams.get('template'));
        }
      }).fail(function (data) {
        console.error("Error when checking if template [{}] is supported by change request.", urlParams.get('template'));
        console.debug(data);
      });
    } else {
      attachButtons();
    }
  }

  $(document).on('xwiki:dom:updated', initialize);
  initialize();
});

